import { Component, Input } from '@angular/core';
import { DashboardData } from '../dashboard-data.model';

@Component({
  selector: 'app-chart',
  templateUrl: './chart.component.html',
  styleUrls: ['./chart.component.css'],
})
export class ChartComponent {
  @Input() data: DashboardData[] = [];

  barChartOptions: any = {
    scaleShowVerticalLines: false,
    responsive: true,
  };
  barChartLabels: string[] = [];
  barChartLegend = true;
  barChartType = 'bar';
  barChartData: any[] = [];

  constructor() {}

  ngOnChanges() {
    this.barChartLabels = this.data.map((entry) => entry.timestamp);
    this.barChartData = [
      {
        data: this.data.map((entry) => entry.impressions),
        label: 'Impressions',
      },
      { data: this.data.map((entry) => entry.clicks), label: 'Clicks' },
      { data: this.data.map((entry) => entry.cost), label: 'Cost' },
      {
        data: this.data.map((entry) => entry.conversions),
        label: 'Conversions',
      },
    ];
  }
}
<div style="display: block">
  <canvas
    baseChart
    [datasets]="barChartData"
    [labels]="barChartLabels"
    [options]="barChartOptions"
    [legend]="barChartLegend"
  >
  </canvas>
</div>
