import { Component } from '@angular/core';
import { DashboardData } from '../dashboard-data.model';
@Component({
  selector: 'app-clicks-chart',
  templateUrl: './clicks-chart.component.html',
  styleUrls: ['./clicks-chart.component.css'],
})
export class ClicksChartComponent {
  @Input() data: DashboardData[] = [];

  barChartOptions: any = {
    scaleShowVerticalLines: false,
    responsive: true,
  };
  barChartLabels: string[] = [];
  barChartLegend = true;
  barChartType = 'bar';
  barChartData: any[] = [];

  constructor() {}

  ngOnChanges() {
    this.barChartLabels = this.data.map((item) => item.timestamp);
    this.barChartData = [
      {
        data: this.data.map((item) => item.impressions),
        label: 'Impressions',
      },
      // ,
      // { data: this.data.map((item) => item.clicks), label: 'Clicks' },
      // { data: this.data.map((item) => item.cost), label: 'Cost' },
      // {
      //   data: this.data.map((item) => item.conversions),
      //   label: 'Conversions',
      // },
    ];
  }
}
