import { Component, OnInit } from '@angular/core';
import { DataService } from '../data.service';
import { DashboardData } from '../dashboard-data.model';

interface ResponseData {
  title: string;
  data: DashboardData[];
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class DashboardComponent implements OnInit {
  response: ResponseData = { title: '', data: [] };
  data: DashboardData[] = [];

  constructor(private dataService: DataService) {}

  fetchData(): void {
    this.dataService.getData().subscribe((response) => {
      console.log('response', response);
      this.response = response;

      this.data = this.response.data;
      // You can perform further operations with the data here
    });
  }

  ngOnInit(): void {
    this.fetchData();
  }
}
<h2>dashboard</h2>

<div class="dashboard">
  <div class="chart">
    <h2>{{ response.title }}</h2>
    <app-overallChart [data]="data" style="width: 40%"></app-overallChart>
  </div>
  <div class="chart">
    <h2>impressions</h2>
    <app-impressions-chart
      [data]="data"
      style="width: 40%"
    ></app-impressions-chart>
  </div>
  <div class="chart">
    <h2>clicks</h2>
    <app-clicks-chart [data]="data" style="width: 40%"></app-clicks-chart>
  </div>
  <div class="chart">
    <h2>Cost</h2>
    <app-cost-chart [data]="data" style="width: 40%"></app-cost-chart>
  </div>
</div>
